tinymce.PluginManager.add('vimages', function(editor, url) {
  var openMediaDialog = function () {
    $bus.$emit('selectImage', editor.vularId)
    $bus.$on('selectedImages',(images, id)=>{
      if(id == editor.vularId){
        var html = ''
        images.forEach(media=>{
          html += '<img src="'+ media.src + '" />';
        })
        editor.insertContent(html);
      }
      $bus.$off('selectedImages', this)
    })
  };
  
  // 注册一个工具栏按钮名称
  editor.ui.registry.addButton('vimages', {
  	icon:"image",
    tooltip: 'Image',
    onAction: function () {
      openMediaDialog();
    }
  });

  // 注册一个菜单项名称 menu/menubar
  editor.ui.registry.addMenuItem('vimages', {
    icon: 'image',
    text: 'Image...',
    onAction: function() {
      openMediaDialog();
    }
  });

  return {
    getMetadata: function () {
      return  {
        //插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
        name: "Insert image",//插件名称
        url: "http://vular.cn", //作者网址
      };
    }
  };
});
